CREATE TABLE logEmailsSent (LogID INT IDENTITY(1,1), EmailFrom VARCHAR(256) DEFAULT NULL, FromName VARCHAR(256) DEFAULT NULL, EmailTo VARCHAR(256) DEFAULT NULL, CC VARCHAR(1024) DEFAULT NULL, BCC VARCHAR(1024) DEFAULT NULL, MailSubject VARCHAR(128) DEFAULT NULL, MailBody VARCHAR(MAX) DEFAULT NULL, AttachmentFilePath VARCHAR(512) DEFAULT NULL, Created DATETIME DEFAULT GETUTCDATE(), CreatedBy INT, Success BIT DEFAULT 0, ErrorMessage VARCHAR(MAX) DEFAULT NULL, StatusCode INT DEFAULT NULL)